<?php $configdb_file=fopen("../php_scripts/configdb.cnf", "r") or $configdb_file=fopen("../../php_scripts/configdb.cnf", "r");$db_type='';$db_host='';$db_name='';$db_user='';$db_pwd='';$org_title='';while(!feof($configdb_file)){$ConnectString=fgets($configdb_file);if(substr($ConnectString,0,2)=="->"){$ExplodeString=explode(",",$ConnectString);$db_type=$ExplodeString[1];$db_host=$ExplodeString[2];$db_name=$ExplodeString[3];$db_user=$ExplodeString[4];$db_pwd=$ExplodeString[5];$org_title=substr($ExplodeString[6],0,-1);break;}}fclose($configdb_file);?>